import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';

const MAIN_BACKGROUNDS_QUERY = gql`
  query MAIN_BACKGROUNDS_QUERY {
    mainPage: mainPages {
      bg_image_1 {
        id
        url
      }
      bg_image_2 {
        id
        url
      }
    }
  }
`;

function GetMedia() {
  const { data, error, loading } = useQuery(MAIN_BACKGROUNDS_QUERY);

  return <p></p>
}

export const media = [GetMedia.data?.bg_image_1, GetMedia.data?.bg_image_1];
console.log(media);
export const mediaByIndex = (index) => media[index % media.length];
