import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import { MdExpandMore, MdExpandLess } from 'react-icons/md';


import { useMenu } from '../../context/menuState';

import useWindowDimensions from '../../lib/windowDimensions';
import { formatUrlToRoute } from '../../helpers/formatUrl';

import {
  MenuDropdownStyles,
  MenuDropdownBtnStyles,
  MenuDropdownItemStyles,
  MenuDropdownMenuStyles
} from './MenuDropdownStyles';


const MenuDropdownItem = React.forwardRef(({ href, onClick, item }, ref) => {
  const { closeMenu } = useMenu();
  return (
    <MenuDropdownItemStyles>
      <a href={href} onClick={() => closeMenu()} ref={ref}>
        {item}
      </a>
    </MenuDropdownItemStyles>
  );
});

const MenuDropdown = React.forwardRef(function MenuDropdown(props, ref) {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const { isOpen } = useMenu();
  const { width } = useWindowDimensions();

  const showDropdown = () => setDropdownOpen(!dropdownOpen);

  const mouseEnter = () => {
    !isOpen && setDropdownOpen(true);
  };

  const mouseLeave = () => {
    !isOpen && setDropdownOpen(false);
  };

  useEffect(() => {
    let isMounted = true;
    if (width >= 850) {
      setDropdownOpen(false);
    }
    return () => {
      isMounted = false;
    };
  }, [width]);

console.log(props);

  return (
    <MenuDropdownStyles onMouseOver={mouseEnter} onMouseLeave={mouseLeave}>
      <div className='dropdown-btns-group'>
        <a href={props.href} ref={ref}>
          {props.title}
        </a>
        <MenuDropdownBtnStyles
          type='button'
          onClick={showDropdown}
          disabled={!isOpen || width > 850}
        >
          {dropdownOpen && isOpen ? <MdExpandLess /> : <MdExpandMore />}
        </MenuDropdownBtnStyles>
      </div>

      {dropdownOpen && (
        <MenuDropdownMenuStyles>
          {props?.categories?.map(category => (
            <Link
              href={{
                pathname: '/products/[products]',
                query: {
                  products: `${formatUrlToRoute(catego.product_title)}`,
                },
              }}
              key={product.id}
              passHref
            >
              {/* <DropdownItemStyles>
              <a>{product.product_title.toLowerCase()}</a>
              </DropdownItemStyles> */}
              <MenuDropdownItem item={product?.product_title} />
            </Link>
          ))}
        </MenuDropdownMenuStyles>
      )}
    </MenuDropdownStyles>
  );
});

export default MenuDropdown;
