import Image from 'next/image';

import { ItemImageStyles, SingleItemStyles } from './SingleItemStyles';

export default function SingleItem({ singleItem }) {
  return (
    <SingleItemStyles>
      <ItemImageStyles>
        <Image src={singleItem.image[0].url} width={400} height={400} />
      </ItemImageStyles>
      <div className='item-description-wrapper'>
        <ItemTitle>{singleItem.item_title}</ItemTitle>
        <h5>${singleItem.price}</h5>
      </div>
    </SingleItemStyles>
  );
}
