  cursor: pointer;
  margin: 0 1rem 1rem 1rem;



  h3 {
    position: absolute;
    top: 40%;
    z-index: 2;
    font-size: 3rem;
    font-weight: 400;
    color: blue;
  }

  .product-item {
    position: relative;
  }

  .image-overlay {
    display: block;
    position: relative;
    &:hover {
      z-index: 99999992;
    background-color: black;
