import { useEffect } from 'react';
import Image from 'next/image';
import { SlideStyles } from './SlideStyles';

export default function Silde({ mediaByIndex, index, setAnimation }) {
  useEffect(() => {
    let mounted = true;
    mounted && setAnimation(false);
    return () => (mounted = false);
  });


  return (
    <SlideStyles>
      <Image
        className='embla__slide__img'
        src={mediaByIndex(index)[0]}
        objectFit='cover'
        layout='fill'
        alt=''
      />
      <div className='image-1'>
        <Image width={200} height={200} src={mediaByIndex(index)[1]} />
      </div>
      <div className='image-2'>
        <Image width={200} height={200} src={mediaByIndex(index)[2]} />
      </div>
      <button className='slide-btn' type='button'>
        see products
      </button>
    </SlideStyles>
  );
}
