import styled from 'styled-components';

const NavStyles = styled.div`
  margin: 0;
  padding: 0;
  position: relative;
  padding-right: 3rem;
  grid-area: navbar;
  display: grid;
  grid-auto-flow: column;
  grid-auto-columns: max-content;
  gap: 0.5rem;
  place-self: center end;
  @media (max-width: 700px) {
    /* transition: all 0.3s; */

  }
  a {
    padding: 0.2rem 1rem;
    text-transform: uppercase;
    color: grey;
    position: relative;
    @media (max-width: 701px) {
      /* display: none; */
    }
    &:after {
      content: '';
      height: 80%;
      width: 1px;
      background: var(--lightGrey);
      position: absolute;
      right: -0.25rem;
    }
    &:hover,
    &:focus {
      text-decoration: none;
      color: var(--black);
      transition: 250ms;
    }
  }

`;

export default NavStyles;
