import { useState, useEffect } from 'react';
import {Slant as Hamburger} from 'hamburger-react';

import useWindowDimensions from '../../../lib/windowDimensions';
import MenuLink from './MenuLink';
import { ItemsMenuStyles, MenuButtonStyles } from './ItemsMenuStyles';


export default function ItemsMenu({ products }) {
  const [isOpen, setOpen] = useState(false);
  const [btnClicked, setBtnClicked] = useState(false);

  const { width } = useWindowDimensions();

 useEffect(() => {
   if (width >= 850) {
     setOpen(false);
   }
 }, [width]);

 function onClick() {
   setBtnClicked(true);
   setOpen(false);
 }

  return (
    <ItemsMenuStyles menuOpen={isOpen} btnClicked={btnClicked}>
      <div className='menu-header'>
        <MenuButtonStyles onClick={()=> onClick}>
          <Hamburger
            hideOutline={false}
            label='Show menu'
            toggled={isOpen}
            toggle={setOpen}
          />
        </MenuButtonStyles>
        <h3 className='main-title'>Products</h3>
      </div>
      <div className='menu-links'>
        {products.map(product => (
          <MenuLink product={product} key={product.id} />
        ))}
      </div>
      <div className='side-menu-links '>
        {products.map(product => (
          <MenuLink product={product} key={product.id} />
        ))}
      </div>
    </ItemsMenuStyles>
  );
}
