import styled from 'styled-components';

const ProductsMainPageStyles = styled.section`
  position: relarive;

  margin-top: 8rem;
  padding-left: 5rem;
  padding-right: 5rem;

  h2 {
    font-size: 4rem;
    text-transform: uppercase;
    font-weight: 400;
  }

  .products_main-page {
    display: flex;
    flex-wrap: scroll;
    overflow-x: scroll;
    overflow-y: none;
  }

  .icon-arrow-left {
    position: absolute;
    left: 0;
    align-items: center;
    font-size: 4rem;
    color: blue;
  }

  .icon-arrow-right {
    position: absolute;
    right: 0;
    font-size: 4rem;
    color: blue;
  }
`;

const ProductMainPageStyles = styled.div`
  .flex-item {
    flex: 0 0 auto;
  }
  img {
    max-width: 300px;
    max-height: 300px;
  }
`;

export { ProductsMainPageStyles, ProductMainPageStyles };
