import React, { useEffect, useState } from 'react';
import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';

import Image from 'next/image';
import img from '../../public/img/img_2.png';
import styled from 'styled-components';

const PRODUCT_CATEGORY = gql`
  query PRODUCT_CATEGORY($start: Int, $limit: Int) {
    products(first: $first, limit:$limit) {
      id
      category
      single_products {
        Name
      }
    }
  }
`;

console.log();

export default function ProductsCategories({}) {
  const { data, error, loading } = useQuery(PRODUCT_CATEGORY);

  console.log(data);
  return (
    <>
      <h1>Products</h1>
      {data?.products.map(c => (
        <h2 key={c.id}>{c.category}</h2>
      ))}
    </>
  );
}
