import styled from 'styled-components';

const Img = styled.div`
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    display: block;
    background: black;
    opacity: 0.1;
    z-index: 2;
`;

const SlideStyles = styled.div`
  background: rgb(240, 240, 240);
  cursor: pointer;
  margin: 0 1rem 1rem 1rem;

  &:hover {
    ${Img} {
      opacity: 0.3;
    }
  }

  h3 {
    position: absolute;
    top: 40%;
    z-index: 2;
    font-size: 3rem;
    font-weight: 400;
    color: blue;
  }

  .product-item {
    position: relative;
  }

  .product-image {
  }
`;

export { SlideStyles, Img};
