import styled from 'styled-components';

const SlideStyles = styled.div`
  background: rgb(240, 240, 240);
  cursor: pointer;
  margin: 0 1rem 1rem 1rem;

  &:hover .image-overlay {
    transform: scaleY(1.1);
    transition: 0.4s;
  }
  &:hover .image-overlay:after {
    background: linear-gradient(transparent 0%, rgba(184, 183, 183, 0.705) 100%)
      repeat-x rgba(233, 232, 232, 0.849);
    transition-property: opacity;
    transition-duration: 0.5s;
  }
  h3 {
    position: absolute;
    top: 40%;
    z-index: 2;
    font-size: 3rem;
    font-weight: 400;
    color: blue;
  }

  .product-item {
    position: relative;
  }

