/* eslint-disable jsx-a11y/click-events-have-key-events */
// import { useQuery } from '@apollo/client';
// import gql from 'graphql-tag';
import Link from 'next/link';
import React, { useState } from 'react';
import { MdExpandMore, MdExpandLess } from 'react-icons/md';
import styled from 'styled-components';
import { useNav } from '../lib/navState';

const NavDropdownStyles = styled.a`
  padding: 0.2rem 1rem;
  text-transform: uppercase;
  color: grey;
  position: relative;
  display: flex;
  cursor: pointer;
  &:hover,
  &:focus {
    text-decoration: none;
    color: var(--black);
    transition: 250ms;
    background: none;
  }
  @media (max-width: 700px) {
    padding-left: 3rem;
    width: 200px;
    border-bottom: 1px solid var(--lightGrey);
    padding-bottom: 1rem;
  }
`;

const DropdownBtnStyles = styled.button`
  padding: 0;
  font-size: 2em;
  display: inline-block;
  outline: none;
  border: none;
  background: transparent;
  cursor: pointer;
  /* position: absolute; */
  @media (max-width: 700px) {
    display: inline-block;

    /* position: fixed;
    right: 5rem; */
  }
  svg {
    padding-bottom: 0.3rem;
    color: grey;
  }
`;

const DropdownMenuStyles = styled.ul`
  background: var(--lightGrey);
  margin: 0;
  li {
    font-size: 1rem;
    padding-left: 2rem;
    text-decoration: none;
    list-style: none;
  }
`;

function DropdownItem() {
  return <li>1</li>;
}

const NavDropdown = React.forwardRef(function NavDropdown(props, ref) {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const { navOpen } = useNav();

  const showDropdown = () => setDropdownOpen(!dropdownOpen);

  return (
    <>
      <NavDropdownStyles href={props.href} ref={ref}>
        {props.title}
      </NavDropdownStyles>
      <DropdownBtnStyles
        type="button"
        onClick={showDropdown}
        disabled={!navOpen}
      >
        {dropdownOpen ? <MdExpandLess /> : <MdExpandMore />}
      </DropdownBtnStyles>

      {navOpen && dropdownOpen && (
        <DropdownMenuStyles>
          <DropdownItem />
        </DropdownMenuStyles>
      )}
    </>
  );
});

export default NavDropdown;
