function formatUrlToDbName(str) {
  return str.toLowerCase().split('_').join(' ');
}

function formatUrlToRoute(str) {
  str = toLowerCase().split(' ').join('_');
  return str.toLowerCase().split(' ').join('_');
}

export { formatUrlToDbName, formatUrlToRoute };
