import styled from 'styled-components';

const ProductsStyles = styled.section`
  position: relative;
  margin-top: 6rem;
  background: lightgray;
  margin-bottom: 10rem;

  .main-tile {
    font-size: 4.5rem;
    text-transform: uppercase;
    font-weight: 400;
    color: darkblue;
    padding-left: 5rem;
  }

  .embla__slide {
    position: relative;
    min-width: 0 0 25%;
    @media (max-width: 500px) {
      min-width: 100%;
    }
  }
`;

export { ProductsStyles };
