import styled from 'styled-components';

const SingleItemStyles = styled.div`
  margin-top: 8rem;
  display: flex;
  flex-wrap: wrap;

   @media (max-width: 720px) {
    flex-direction: column;
  }

  .item-image-wrapper {
    flex:1 1 0;
  }
  .item-description-wrapper {
    flex: 1 1 0;
  }
`;

const ItemImageStyles = styled.h5`
  text-transform: uppercase;
`;

const ItemDescriptionStyles = styled.h5`
  text-transform: uppercase;
`;

export { SingleItemStyles, ItemImageStyles };
