import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import Image from 'next/image';
import Products from './Product';

import { ProductMainPageStyles, ProductsMainPageStyles } from './ProductsMainPageStyles';

const PRODUCTS = gql`
  query PRODUCTS {
    products {
      id
      product_title
      product_categories {
        single_products {
          title
          description
          price
          image {
            url
          }
        }
      }
    }
  }
`;


function ProductMainPage({product}) {
  return (
    <ProductMainPageStyles>
      <img
        src={product.product_categories[1]?.single_products[0]?.image?.url}
        alt=''
      />
    </ProductMainPageStyles>
  );
};

export default function ProductsMainPage({}) {

    const { data, error, loading } = useQuery(PRODUCTS);

  console.log(data);

  return (
    <ProductsMainPageStyles>
      <h2>Products</h2>

      <div className='products_main-page'>
        {data?.products.map(product => (

          <ProductMainPage key={product.id} product={product} />

        ))}

      </div>
    </ProductsMainPageStyles>
  );
}
