import styled from 'styled-components';

const DropdownStyles = styled.div`
  position: relative;
  align-self: center;
  display: inline-block;
  .dropdown-btns-group {
    @media (max-width: 850px) {
      display: flex;
    }
  }
`;

const DropdownBtnStyles = styled.button`
  padding: 0;
  outline: none;
  border: none;
  background: transparent;
  cursor: pointer;
  display: none;
  @media (max-width: 850px) {
    display: block;
    position: fixed;
    right: 1rem;
  }
  svg {
    color: grey;
    transform: scale(2);
  }
`;

const DropdownMenuStyles = styled.ul`
  background: var(--lightGrey);
  opacity: 1;
  z-index: 2;
  width: 50rem;
  position

  @media (min-width: 850px) {
    background: var(--offWhite);
    border: 1px solid var(--lightGrey);
    position: absolute;
    top: 60%;
    margin-left: -5rem;
    padding: 1rem;
    display: grid;
    grid-template-columns: 1fr 1fr;
  }
`;

const DropdownItemStyles = styled.li`
  list-style: none;
  text-transform: capitalize;
  cursor: pointer;
  line-height: 1.5rem;
  padding: 2rem;
  a {
    text-decoration: none;
    color: grey;
    font-size: 1.5rem;
    &:hover {
      text-decoration: none;
      color: var(--black);
      transition: 250ms;
    }
  }
`;

export {
  DropdownStyles,
  DropdownBtnStyles,
  DropdownMenuStyles,
  DropdownItemStyles,
};
