import styled from 'styled-components';

const SingleItemStyles = styled.div`
  margin-top: 8rem;
  display: flex;
  flex-wrap: wrap;

   @media (max-width: 720px) {
    flex-direction: column;
  }

  .item-image {
    flex:1 1 0;
  }
  .item-description {
    flex: 1 1 0;
  }
`;

export { SingleItemStyles };
