import gql from 'graphql-tag';

import { useQuery } from '@apollo/client';
import { useRef } from 'react';
import useWindowDimensions from '../../lib/windowDimensions';

import { MdKeyboardArrowLeft, MdKeyboardArrowRight } from 'react-icons/md';

import Image from 'next/image';

import {
  ProductMainPageStyles,
  ProductsMainPageStyles,
} from './ProductsMainPageStyles';

const PRODUCTS = gql`
  query PRODUCTS {
    products {
      id
      product_title
      product_categories {
        single_products {
          title
          description
          price
          image {
            url
          }
        }
      }
    }
  }
`;

function ProductMainPage({ product }) {

  return (
    <ProductMainPageStyles>
      <h3>{product.product_title}</h3>
      <div className='product-item'>
        <Image
          className='product-image'
          src={product.product_categories[0]?.single_products[0]?.image?.url}
          width={300}
          height={300}
          layout='responsive'
          // objectFit='cover'
        />
      </div>
    </ProductMainPageStyles>
  );
}

export default function ProductsMainPage({}) {
  const { data, error, loading } = useQuery(PRODUCTS);


  // horizontal scroll for products
  const scrollRef = useRef();
  const { width } = useWindowDimensions();
  let scrollSize = 300;

 width < 500 ? scrollSize = 250 + 15 + 7.5 : scrollSize;

 console.log(scrollRef);
 console.log(scrollSize);

  const handleHorizontalScroll = direction => {
    if (direction === 'left') {
      scrollRef ? (scrollRef.current.scrollLeft -= scrollSize) : null;
    } else {
      // scrollRef ? (scrollRef.current.scrollLeft += scrollSize) : null;
      scrollRef.current.scrollIntoView({ inline: 'center' });
    }
  };

  return (
    <ProductsMainPageStyles>
      <h2>Products</h2>
      <button
        className='icon-arrow-left'
        onClick={() => handleHorizontalScroll('left')}
      >
        <MdKeyboardArrowLeft />
      </button>

      <div className='products_main-page' ref={scrollRef}>
        {data?.products.map(product => (
          <ProductMainPage key={product.id} product={product} />
        ))}
      </div>

      <button
        className='icon-arrow-right'
        onClick={() => handleHorizontalScroll('right')}
      >
        <MdKeyboardArrowRight />
      </button>
    </ProductsMainPageStyles>
  );
}
