import styled from 'styled-components';

const SlideStyles = styled.div`
  position: relative;
  background: white;

  width: 300px;
  height: 300px;
  /* margin: 0 1rem 1rem 1rem; */
  margin: 0 15px 15px 15px;

  h3 {
    position: absolute;
    top: 40%;
    z-index: 2;
    font-size: 3rem;
    font-weight: 400;
    color: blue;
  }

  .product-item {


    width: 300px;
    height: 300px;

    @media (max-width: 500px) {
      width: 250px;
      height: 250px;
    }
  }
  .product-image {
  }
`;

export { SlideStyles };
