import styled from 'styled-components';

const ProductsStyles = styled.section`
  position: relative;
  margin-top: 6rem;
  background: lightgray;
  margin-bottom: 10rem;

  .main-tile {
    font-size: 4.5rem;
    text-transform: uppercase;
    font-weight: 400;
    color: darkblue;
    padding-left: 5rem;
  }

  .embla__slide {
    position: relative;
    min-width: 25%;
    @media (max-width: 500px) {
      min-width: 50%;
    }
  }

  .item-title {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 3.5rem;
    text-transform: uppercase;
    font-weight: 400;
    color: rgb(6, 6, 192);
    z-index: 3;    
    @media (max-width: 500px) {
      font-size: 2.8rem;
    }
  }
`;

export { ProductsStyles };
