import styled from 'styled-components';

const ItemsMenuStyles = styled.div`

  /* margin-top: calc(var(--navHeight) + var(--searchHeight));

  background: lightblue;
  overflow: hidden;
  width: 100%;
  z-index: 1;
  .menu-header {
    position: relative;
    height: 100px;
    padding-top: 1rem;
    padding-left: 2rem;
  }
  .main-title {
    font-size: 3.5rem;
    text-transform: uppercase;
    font-weight: 400;
    color: darkblue;
    margin: 0;
    transition: transform 0.35s;
    @media (max-width: 850px) {
      transform: translateX(6rem);
    }
  }
  .menu-links {
    padding-left: 2rem;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    @media (max-width: 850px) {
      display: none;
    }
  }

  .side-menu-links {
    flex-direction: column;
    align-items: flex-start;
    background: lightblue;
    display: none;
    position: absolute;
    width: 300px;
    padding-left: 3rem;
    opacity: 0;
    transform: translateX(-100%);
    transition: all 0.35s;
    z-index: 1;

    ${props => props.menuOpen && `transform: translateX(0%);opacity:1;`};
    ${props =>
      !props.btnClicked &&
      ` -webkit-transition: none !important;
       -moz-transition: none !important;
        -ms-transition: none !important;
        -o-transition: none !important;`};
    @media (max-width: 850px) {
      display: block;
    }
  } */
`;

const MenuButtonStyles = styled.div`
  display: none;
  transition: all 0.5s;
  @media (max-width: 850px) {
    display: block;
    position: absolute;
    top: 2rem;
    left: 2rem;
  }
`;

export { ItemsMenuStyles, MenuButtonStyles };
