import { useState } from 'react';
import Hamburger from 'hamburger-react';

import MenuLink from './MenuLink';
import { ItemsMenuStyles, MenuButtonStyles } from './ItemsMenuStyles';

export default function ItemsMenu({ products }) {
  const [isOpen, setOpen] = useState(false);
   const [btnClick, setBtnClick] = useState(false);

  return (
    <ItemsMenuStyles menuOpen={isOpen} btnClick={btnCkick}>
      <div className='menu-header'>
        <MenuButtonStyles onClick={() => setBtnClick(true)}>
          <Hamburger
            hideOutline={false}
            label='Show menu'
            toggled={isOpen}
            toggle={setOpen}
          />
        </MenuButtonStyles>
        <h3 className='main-title'>Products</h3>
      </div>
      <div className='menu-links'>
        {products.map(product => (
          <MenuLink product={product} key={product.id} />
        ))}
      </div>
      <div className='side-menu-links '>
        {products.map(product => (
          <MenuLink product={product} key={product.id} />
        ))}
      </div>
    </ItemsMenuStyles>
  );
}
