import React, { useEffect, useRef } from 'react';
import { useRouter } from 'next/router';
import gql from 'graphql-tag';
import { useQuery } from '@apollo/client';
import Link from 'next/link';
import Hamburger from 'hamburger-react';

import { useNav } from '../../context/navState';
import useWindowDimensions from '../../lib/windowDimensions';

import NavDropdown from './NavDropdown';
import { NavStyles, NavButtonStyles } from './NavStyles';
import Search from './Search';
import { formatUrlToRoute } from '../../helpers/formatUrl';

const PRODUCTS = gql`
  query PRODUCTS1 {
    services {
      service
      id
      items {
        id
        title
      }
    }
  }
`;

export default function Nav() {
  const { data, error, loading } = useQuery(PRODUCTS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  // services to spread in nav
  const services = data?.services;

const navRef = useRef();

  const { navOpen, toggleNav, closeSideNav, navBtnClick, setNavBtnClick } =
    useNav();

  const { width } = useWindowDimensions();

  // close toggled nav on click outside
  useEffect(() => {
    //  click outside nav handler
    const handleClickOutside = event => {
      if (
        navOpen &&
        !navRef.current.contains(event.target)
      ) {
        closeSideNav();
      }
    };

    //  click outside nav listener
    document.addEventListener('mousedown', handleClickOutside);

    // cleanup the event listener
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [navOpen]);

  // close nav when width more than 850px
  useEffect(() => {
    let isMounted = true;
    if (width >= 850) {
      closeSideNav();
    }
    return () => {
      isMounted = false;
    };
  }, [width]);

 const router = useRouter();


  const LinkBtn = React.forwardRef(({ href, title }, ref) => {
    return (
      <a href={href} onClick={() => closeSideNav()} ref={ref}>
        {title}
      </a>
    );
  });


  return (
    <>
      <NavStyles
        open={navOpen}
        btnClick={navBtnClick}
        width={width}
        ref={navRef}
      >
        <div className='nav-links'>
          <Link
            href='/'
            passHref
            className={
              formatUrlToRoute(router.asPath.split('/')[1]) == ''
                ? 'active-link link-item'
                : 'link-item'
            }
          >
            <LinkBtn title={'home'} />
          </Link>
          <Link href='/about' className='link-item' passHref>
            <LinkBtn title={'about'} />
          </Link>
          {services.map(service => (
            <Link key={service.id} href={`/${service.service}`} passHref>
              <NavDropdown title={service.service} items={service.items} />
            </Link>
          ))}
          <Link href='/gallery' passHref>
            <LinkBtn title={'gallery'} />
          </Link>
          <Link href='/contacts' passHref>
            <LinkBtn title={'contacts'} />
          </Link>
        </div>
        <NavButtonStyles onClick={() => setNavBtnClick(true)}>
          <Hamburger
            hideOutline={false}
            label='Show menu'
            toggled={navOpen}
            toggle={toggleNav}
          />
        </NavButtonStyles>
      </NavStyles>
      <Search />
    </>
  );
}
