import styled, { keyframes } from 'styled-components';

const fadeInR = keyframes`
    0% {
      transform: translate(50%);
      opacity: 0;
    }
    100% {
        transform: translate(0%);
        opacity: 1;
    }
  `;
const fadeInL = keyframes`
    0% {
      opacity: 0;
      transform: translate(-50%);
    }
    100% {
        transform: translate(0%);
        opacity: 1;
    }
  `;


const SlideStyles = styled.div`
  position: relative;
  height: 100%;

  .image-1 {
    display: inline-block;
    position: absolute;
    left: 30%;
    top: 18em;

    animation: ${fadeInL} 1.3s;
    max-width: 200px;
    max-height: 200px;
    @media (max-width: 601px) {
      max-width: 40%;
      max-height: 40%;
    }
  }
  .image-2 {
    display: inline-block;
    position: absolute;
    right: 30%;
    top: 8em;
    animation: ${fadeInR} 1.3s;

    max-width: 200px;
    max-height: 200px;
    @media (max-width: 601px) {
      max-width: 40%;
      max-height: 40%;
    }
  }

  .slide-btn {
    position: absolute;
    top: 10%;
    right: 10%;
  }
`;

export {SlideStyles}
