import styled from 'styled-components';

const SingleItemStyles = styled.div`
  margin-top: 8rem;
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  @media (max-width: 720px) {
    flex-direction: column;
  }

  .item-image {
    flex: 0 1 ;
  }
`;

export { SingleItemStyles };
