import styled from 'styled-components';

const ItemsMenuStyles = styled.nav`
  background: lightblue;

  .menu-header {
    height: 100px;
    padding-top: 1rem;
    padding-left: 2rem;
  }

  .main-title {
    font-size: 3.5rem;
    text-transform: uppercase;
    font-weight: 400;
    color: darkblue;
    margin: 0;
    transition: transform 0.35s;
    @media (max-width: 850px) {
      transform: translateX(6rem);
    }
  }
  .menu-links {
    padding-left: 2rem;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    @media (max-width: 850px) {
      display: none;
  }
}

  .side-menu-links {
    display: none;
    @media (max-width: 850px) {
      display: block;

    }
  }
`;

const MenuButtonStyles = styled.div`
  display: none;
  transition: all 0.5s;
  @media (max-width: 850px) {
    display: block;
    position: absolute;
    top: 2rem;
    left: 2rem;
  }
`;

export { ItemsMenuStyles, MenuButtonStyles };
