import styled from 'styled-components';

const SingleItemStyles = styled.div`

  margin-top: 8rem;
  display: flex;
  flex-wrap: wrap; 

  /* align-content: flex-start;
  justify-content: start; */

  @media (max-width: 720px) {
    flex-direction: column;
  }

  .item-image {
    width: 100%
    flex-grow: 1;

  }
  .item-description {

    flex-grow: 1;
  }
`;

export { SingleItemStyles };
