import styled from 'styled-components';

const SlideStyles = styled.div`
  background: rgb(240, 240, 240);
  cursor: pointer;
  margin: 0 1rem 1rem 1rem;

  &:hover .img-overlay {
    display: block;
    position: relative;

  }

  h3 {
    position: absolute;
    top: 40%;
    z-index: 2;
    font-size: 3rem;
    font-weight: 400;
    color: blue;
  }

  .product-item {
    position: relative;
  }

  .product-image {
  }
`;

export { SlideStyles };
